from pathlib import Path

import librosa
import numpy as np
import matplotlib.pyplot as plt

# %% ../../nbs/10_plot.ipynb 3
def plot_waveform(
    waveform: np.ndarray,
    sample_rate: int,
) -> None:
    """
    Plots a waveform.

    Args:
        waveform: The audio waveform to plot.
        sample_rate: The sample rate of the audio waveform.
        title: The title of the plot.
    """
    librosa.display.waveshow(
        waveform,
        sr=sample_rate,
    )

# %% ../../nbs/10_plot.ipynb 4
def plot_spectrogram(
    spectrogram: np.ndarray,
    *,
    sample_rate: int = 22050,
    fmax: int | None = None,
) -> None:
    """
    Plots a spectrogram.

    Args:
        spectrogram: The spectrogram to plot.
        sample_rate: Sample rate for proper frequency axis scaling.
        fmax: Maximum frequency to display (optional).
    """
    librosa.display.specshow(
        spectrogram,
        x_axis='time',
        y_axis='linear',
        fmax=fmax,
        sr=sample_rate,
    )
