hudson.tasks.Shell.command:
#!/bin/bash # # Sample script showing how the yaml !include-raw-verbatim: tag can be used # to inline scripts that are maintained outside of the jenkins # job yaml configuration.
echo "hello world"
exit 0

#!/bin/bash # # sample script to check that variables aren't expanded # when using the !include-raw-verbatim: application yaml tag
VAR1="hello" VAR2="world" VAR3="${VAR1} ${VAR2}"
[[ -n "${VAR3}" ]] && { # this next section is executed as one echo "${VAR3}" exit 0 }
