def get_sym_axis_temp(sym_key, N=2):
    if sym_key == 'None' or sym_key is None:
        return None
    if sym_key in sym_axis_temp:
        return sym_axis_temp[sym_key]
    else:
        if sym_key == "D(=1): n-fold Pyramidal Item":
            l_ = []
            l_.append(
                {
                    "axis_l": [
                        [
                            0.0,
                            0.0,
                            1.0
                        ]
                    ],
                    "num": N - 1
                }
            )
            return l_

        elif sym_key == "D(>1): n-fold Prismatic Item":
            l_ = []
            l_.append(
                        {
                            "axis_l": [
                                [
                                    0.0,
                                    0.0,
                                    1.0
                                ]
                            ],
                            "num": N - 1
                        }
                    )
            for i_ in range(N):
                a_ = i_/N * np.pi
                l_.append(
                            {
                                "axis_l": [
                                    [
                                        np.cos(a_),
                                        np.sin(a_),
                                        0.0
                                    ]
                                ],
                                "num": 1
                            }
                        )
            return l_
        else:
            return None
    

    
    
    
    
    
