svg.foreignObject:
    metadata:
  name: "React Application"
  framework: "react"
  version: "18.0.0"

config:
  build_tool: "vite"
  typescript: true
  routing: "react-router"
  state_management: "context"

structure:
  app_component: |
    import React from 'react';
    import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
    import { ThemeProvider } from './contexts/ThemeContext';
    import Header from './components/Header';
    import Footer from './components/Footer';
    import Home from './pages/Home';
    import About from './pages/About';
    import Contact from './pages/Contact';
    import './App.css';

    function App() {
      return (
        <ThemeProvider>
          <Router>
            <div className="app">
              <Header />
              <main className="main-content">
                <Routes>
                  <Route path="/" element={<Home />} />
                  <Route path="/about" element={<About />} />
                  <Route path="/contact" element={<Contact />} />
                </Routes>
              </main>
              <Footer />
            </div>
          </Router>
        </ThemeProvider>
      );
    }

    export default App;

  header_component: |
    import React from 'react';
    import { Link, useLocation } from 'react-router-dom';
    import './Header.css';

    const Header = () => {
      const location = useLocation();

      const navItems = [
        { path: '/', label: 'Home', icon: '🏠' },
        { path: '/about', label: 'About', icon: 'ℹ️' },
        { path: '/contact', label: 'Contact', icon: '📧' }
      ];

      return (
        <header className="header">
          <div className="container">
            <Link to="/" className="brand">
              {{app_name}}
            </Link>
            <nav className="nav">
              {navItems.map((item) => (
                <Link
                  key={item.path}
                  to={item.path}
                  className={`nav-link ${location.pathname === item.path ? 'active' : ''}`}
                >
                  <span className="nav-icon">{item.icon}</span>
                  {item.label}
                </Link>
              ))}
            </nav>
          </div>
        </header>
      );
    };

    export default Header;

  home_page: |
    import React, { useState, useEffect } from 'react';
    import './Home.css';

    const Home = () => {
      const [features, setFeatures] = useState([]);

      useEffect(() => {
        // Simulate loading scraped content
        setFeatures([
          {
            id: 1,
            title: 'Modern Framework',
            description: 'Built with React 18 and modern hooks',
            icon: '⚛️'
          },
          {
            id: 2,
            title: 'Responsive Design',
            description: 'Works perfectly on all devices',
            icon: '📱'
          },
          {
            id: 3,
            title: 'Fast Performance',
            description: 'Optimized for speed and efficiency',
            icon: '⚡'
          }
        ]);
      }, []);

      return (
        <div className="home">
          <section className="hero">
            <div className="container">
              <h1>Welcome to {{app_name}}</h1>
              <p>A modern web application generated from scraped content using WhyML</p>
              <button className="cta-button">Get Started</button>
            </div>
          </section>

          <section className="features">
            <div className="container">
              <h2>Features</h2>
              <div className="features-grid">
                {features.map((feature) => (
                  <div key={feature.id} className="feature-card">
                    <div className="feature-icon">{feature.icon}</div>
                    <h3>{feature.title}</h3>
                    <p>{feature.description}</p>
                  </div>
                ))}
              </div>
            </div>
          </section>
        </div>
      );
    };

    export default Home;

dependencies:
  - "react@^18.0.0"
  - "react-dom@^18.0.0"
  - "react-router-dom@^6.0.0"
  - "vite@^4.0.0"
  - "@vitejs/plugin-react@^4.0.0"

    metadata:
  name: "Progressive Web App"
  type: "pwa"

config:
  manifest:
    name: "{{app_name}} PWA"
    short_name: "{{app_name}}"
    description: "Progressive Web App generated from scraped content"
    start_url: "/"
    display: "standalone"
    theme_color: "{{primary_color}}"
    background_color: "{{background_color}}"
    icons:
      - src: "/icons/icon-192.png"
        sizes: "192x192"
        type: "image/png"
      - src: "/icons/icon-512.png"
        sizes: "512x512"
        type: "image/png"

  service_worker: |
    const CACHE_NAME = 'whyml-pwa-v1';
    const urlsToCache = [
      '/',
      '/static/css/main.css',
      '/static/js/main.js',
      '/manifest.json'
    ];

    self.addEventListener('install', (event) => {
      event.waitUntil(
        caches.open(CACHE_NAME)
          .then((cache) => cache.addAll(urlsToCache))
      );
    });

    self.addEventListener('fetch', (event) => {
      event.respondWith(
        caches.match(event.request)
          .then((response) => {
            if (response) {
              return response;
            }
            return fetch(event.request);
          }
        );
      );
    });

  features:
    - offline_support: true
    - push_notifications: true
    - background_sync: true
    - install_prompt: true

    const fs = require('fs');
const path = require('path');
const yaml = require('js-yaml');

class ReactGenerator {
  constructor(manifestPath) {
    this.manifest = yaml.load(fs.readFileSync(manifestPath, 'utf8'));
    this.outputDir = 'apps/react-app';
  }

  generate() {
    this.createDirectoryStructure();
    this.generatePackageJson();
    this.generateViteConfig();
    this.generateComponents();
    this.generatePages();
    this.generateStyles();
    console.log('React app generated successfully!');
  }

  createDirectoryStructure() {
    const dirs = [
      'src/components',
      'src/pages',
      'src/contexts',
      'src/hooks',
      'src/utils',
      'public'
    ];

    dirs.forEach(dir => {
      fs.mkdirSync(path.join(this.outputDir, dir), { recursive: true });
    });
  }

  generatePackageJson() {
    const packageJson = {
      name: this.manifest.metadata.name.toLowerCase().replace(/\s+/g, '-'),
      version: this.manifest.metadata.version,
      type: 'module',
      scripts: {
        dev: 'vite',
        build: 'vite build',
        preview: 'vite preview'
      },
      dependencies: Object.fromEntries(
        this.manifest.dependencies.map(dep => {
          const [name, version] = dep.split('@');
          return [name, version || 'latest'];
        })
      ),
      devDependencies: {
        '@types/react': '^18.0.0',
        '@types/react-dom': '^18.0.0',
        'typescript': '^5.0.0'
      }
    };

    fs.writeFileSync(
      path.join(this.outputDir, 'package.json'),
      JSON.stringify(packageJson, null, 2)
    );
  }

  generateComponents() {
    const components = this.manifest.structure;

    Object.entries(components).forEach(([key, content]) => {
      if (key.includes('component')) {
        const componentName = this.toPascalCase(key.replace('_component', ''));
        const filePath = path.join(this.outputDir, 'src/components', `${componentName}.jsx`);

        // Process template variables
        const processedContent = this.processTemplate(content);
        fs.writeFileSync(filePath, processedContent);
      }
    });
  }

  generatePages() {
    const pages = this.manifest.structure;

    Object.entries(pages).forEach(([key, content]) => {
      if (key.includes('page')) {
        const pageName = this.toPascalCase(key.replace('_page', ''));
        const filePath = path.join(this.outputDir, 'src/pages', `${pageName}.jsx`);

        const processedContent = this.processTemplate(content);
        fs.writeFileSync(filePath, processedContent);
      }
    });
  }

  processTemplate(content) {
    const variables = this.manifest.variables || {};
    let processed = content;

    Object.entries(variables).forEach(([key, value]) => {
      const regex = new RegExp(`{{${key}}}`, 'g');
      processed = processed.replace(regex, value);
    });

    return processed;
  }

  toPascalCase(str) {
    return str.replace(/(^\w|_\w)/g, match => 
      match.replace('_', '').toUpperCase()
    );
  }
}

// Usage
const generator = new ReactGenerator('manifests/react-app.yaml');
generator.generate();

