from pyglow.mapping import get_style


class Style:
    BOLD = get_style("bold")
    DIM = get_style("dim")
    ITALIC = get_style("italic")
    UNDERLINE = get_style("underline")
    BLINK = get_style("blink")
    REVERSE = get_style("reverse")
    HIDDEN = get_style("hidden")
    STRIKE = get_style("strike")
